package com.nuverax.pdf

import com.nuverax.pdf.data.NxJsonSource
import java.io.File
fun main() {
    val source = NxJsonSource()
    source.read(File("source-example.json"))
    val setup = NxTemplate(
        source
    )
    setup.load(File("example.json"), File("example.pdf"))
}
